import { Component } from '@angular/core';
import { EmployeeService } from 'src/app/service/employee.service';

@Component({
  selector: 'app-get-all-employee',
  templateUrl: './get-all-employee.component.html',
  styleUrls: ['./get-all-employee.component.css']
})
export class GetAllEmployeeComponent {

 employees:any = [];

  constructor(private employeeService: EmployeeService){}

  ngOnInit(){
    this.getAllEmployee();
  }

  getAllEmployee(){
    this.employeeService.getAllEmployee().subscribe((res)=>{
      console.log(res);
      this.employees = res;
    })
  }

  deleteEmployee(id: number){
    this.employeeService.deleteEmployee
  }
}
<button class="btn-new" routerLink="/employee">Post New Employee</button>
<table>
    <thead>
        <tr>
            <th>Emp. Number</th>
            <th>Emp. Name</th>
            <th>Emp. Mobile</th>
            <th>Emp. Email</th>
            <th>Action</th>
        </tr>
    </thead>
    <tbody>
        <tr *ngFor="let employee of employees">
            <td>{{employee.empNo}}</td>
            <td>{{employee.empName}}</td>
            <td>{{employee.mobile}}</td>
            <td>{{employee.email}}</td>
            <td>
                <button class="btn-update" routerLink="/employee/{{employee.id}}">Update</button>
            </td>
        </tr>
    </tbody>
</table>
