import { Component, OnInit } from '@angular/core';
import { NgForm } from '@angular/forms';
import { EmployeeService } from 'src/app/service/employee.service';
import { UserAuthService } from 'src/app/service/user-auth.service';
import { map, catchError } from 'rxjs/operators';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit{
constructor(private employeeService:EmployeeService, private userAuthService:UserAuthService){

}
ngOnInit(): void {

}
login(loginForm: NgForm) {
  this.employeeService.login(loginForm.value)
  .pipe(
    map((response: any) => {
      console.log(response.jwtToken);
      console.log(response.user.role);
      // You can return processed data here if needed
      return response;
    }),
    catchError((error) => {
      console.log(error);
      // Handle error if needed
      throw error; // Rethrow the error to keep it propagating
    })
  )
  .subscribe();
}
}
